mapper.insert:
insert
  into cm_notice
  (
    cmno_no
  , cmno_title
  , cmno_content
  )
  values
  (
    seq_cnno.nextval
  , #{cmNoticeTitle}
  , #{cmNoticeContent}
  )

mapper.update:
update
       cm_notice
   set cmno_title = #{cmNoticeTitle}
     , cmno_content = #{cmNoticeContent}
     , modify_date = sysdate
 where cmno_no = #{cmNoticeNo}

mapper.insert:
insert
  into community
  (
    cm_no
  , mem_no
  , mem_id
  , cm_category
  , cm_title
  , cm_content
  , cm_image
  )
  values
  (
    seq_cno.nextval
  , #{memNo}
  , #{memId}
  , #{cmCategory}
  , #{cmTitle}
  , #{cmContent}
  , #{cmImage}
  )

mapper.update:
update
       community
   set cm_category = #{cmCategory}
     , cm_title = #{cmTitle}
     , cm_content = #{cmContent}
     , cm_image = #{cmImage}
     , modify_date = sysdate
 where cm_no = #{cmNo}
