mapper.insert:
insert
  into member
  (
  	mem_no
  , mem_id
  , mem_pwd
  , mem_name
  , phone
  , email

  )
  values
  (
    seq_mno.nextval
  , #{memId}
  , #{memPwd}
  , #{memName}
  , #{phone}
  , #{email}

  )

mapper.update:
update
	   member
   set mem_pwd = #{memPwd}
     , mem_name = #{memName}
     , phone = #{phone}
     , email = #{email}
     , mem_post = #{memPost}
     , mem_Road = #{memRoad}
     , mem_Detail = #{memDetail}
 where mem_id = #{memId}

